                return "Poor"
        elif book_genre == "Contemporary":
            if keywords == "animals":
                return "Excellent"
            elif keywords == "funny":
                return "Excellent"
            elif keywords == "contemporary":
                return "Excellent"
            else:
                return "Poor"
        elif book_genre == "Animals":
            if keywords == "funny":
                return "Excellent"
            elif keywords == "contemporary":
                return "Excellent"
            elif keywords == "animals":
                return "Excellent"
